import React,{useEffect,useState} from 'react'

function App() {
  const[state,setState] =useState([])
  useEffect(()=>{
     fetch('/api').then(res=>res.json()).then(data=>setState(data))
  },[])



  const submithandler = (()=>{
    fetch('/',{
      method:'POST',
      body:JSON.stringify({
        user:'goremi1'
      })
    })
  })


  return (
   <>
  <h1 onClick={submithandler}> {state.members[0]}</h1>
   </>
  );
}

export default App;
